.n-tell {
    position: fixed;
    bottom: 0;
    z-index: 9999;
}
.n-tell-phone-ring {
    position: relative;
    visibility: visible;
    background-color: transparent;
    width: 110px;
    height: 110px;
    cursor: pointer;
    z-index: 11;
    left: 0;
    bottom: 0px;
    display: block;
}
.n-tell-cricle {
    width: 85px;
    height: 85px;
    top: 12px;
    left: 12px;
    position: absolute;
    border-radius: 100%;
    border: 2px solid #35a249;
    background-color: transparent;
    -webkit-animation: phonering-cricle 1.2s infinite ease-in-out;
    animation: phonering-cricle 1.2s infinite ease-in-out;
    transition: all .5s;
    opacity: 0.5;
    -webkit-transform-origin: 50% 50%;
    -ms-transform-origin: 50% 50%;
    transform-origin: 50% 50%;
}
.n-tell-cricle-fill {
    width: 55px;
    height: 55px;
    top: 27px;
    left: 27px;
    background-color: rgba(53, 162, 73 , 0.7);
    border-radius: 100%;
    position: absolute;
    border: 2px solid transparent;
    -webkit-animation: phonering-circle-fill 2.3s infinite ease-in-out;
    animation: phonering-circle-fill 2.3s infinite ease-in-out;
    transition: all .5s;
    -webkit-transform-origin: 50% 50%;
    -ms-transform-origin: 50% 50%;
    transform-origin: 50% 50%;
}
.n-tell-img-cricle {
    background-color: #35a249;
    width: 38px;
    height: 38px;
    top: 35px;
    left: 35px;
    position: absolute;
    background-size: 20px;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    border-radius: 100%;
    border: 2px solid transparent;
    -webkit-animation: phonering-circle-img 1s infinite ease-in-out;
    animation: phonering-circle-img 1s infinite ease-in-out;
    -webkit-transform-origin: 50% 50%;
    -ms-transform-origin: 50% 50%;
    transform-origin: 50% 50%;
    align-items: center;
    justify-content: center;
}

.n-tell-img-cricle .n-pps-btn-img {
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
}
.n-tell-img-cricle .n-pps-btn-img img {
    width: 25px;
    height: 25px;
}
.n-tell .n-tell-bar {
    position: absolute;
    background: rgba(53, 162, 73 , 0.75);
    height: 40px;
    width: 150px;
    line-height: 40px;
    border-radius: 3px;
    background-size: 100%;
    cursor: pointer;
    transition: all 0.8s;
    -webkit-transition: all 0.8s;
    z-index: 9;
    padding: 0 10px;
    left: 35px;
    top: 35px;
    border-radius: 50px !important;
